import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { UserService } from 'src/app/services/user.service';



@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {

  msg : string = ''

  constructor(private userService : UserService){}


  passwordVerification() {
    let password = document.getElementById('password') as HTMLInputElement;
    let confirmPassword = document.getElementById('confirmPassword') as HTMLInputElement;
    let errorMessage = document.getElementById('error') as HTMLElement;
    let passwordValidation = true;

    if (password.value !== confirmPassword.value) {
      passwordValidation = false;
      confirmPassword.value = '';
      errorMessage.innerHTML = "Passwords Doesn't Match";
      return passwordValidation
    } 
    else {
      passwordValidation = true;
      errorMessage.innerHTML = ''
      return passwordValidation
    }
  }

  // emailValidation(){
  //   email: String
  //   if (signUpForm.valid){
  //     return true
  //   }
  //   else{
  //     return false
  //   }
  // }



  submit(signUpForm: NgForm) {
    this.passwordVerification();
    console.log(this.passwordVerification())
    if (this.passwordVerification() == true){
      this.userService.userSignup(signUpForm).subscribe((res: {statusCode : Number, msg : string})=>{
        console.log(res.msg,res.statusCode)
        console.log(signUpForm)
        let errorMessage = document.getElementById('error') as HTMLElement;
        errorMessage.innerHTML = res.msg

      })
    }
  }


}
