import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, NgForm, ReactiveFormsModule } from '@angular/forms';

import { UserRoutingModule } from './user-routing.module';
import { UserComponent } from './user.component';
import { UserHomeComponent } from './components/user-home/user-home.component';
import { UserNavComponent } from './components/user-nav/user-nav.component';
import { UserfooterComponent } from './components/userfooter/userfooter.component';
import { RouterModule } from '@angular/router';
import { NewpostComponent } from './components/pages/newpost/newpost.component';
import { AuthGuardService } from './services/auth-guard.service';
import { UploadpostService } from './services/uploadpost.service';
import { MatMenuModule } from '@angular/material/menu';
import { ProfileComponent } from './components/pages/profile/profile.component';
import { PostModalComponent } from './components/pages/profile/post-modal/post-modal.component';
import { MatDialogModule } from '@angular/material/dialog';
import { UserProfileComponent } from './components/pages/user-profile/user-profile.component';
import { HttpClient } from '@angular/common/http';



@NgModule({
  declarations: [
    UserComponent,
    UserHomeComponent,
    UserNavComponent,
    UserfooterComponent,
    NewpostComponent,
    ProfileComponent,
    PostModalComponent,
    UserProfileComponent,
  ],
  imports: [
    CommonModule,
    UserRoutingModule,
    RouterModule,
    FormsModule,
    ReactiveFormsModule,
    MatMenuModule,
    MatDialogModule
  ],
  providers: [
    AuthGuardService
  ]
})
export class UserModule { 
  details: any
  media : any[] = [] 
  numberOfPosts: number = 0
  id:any = localStorage.getItem('userId')

  images :any [] = []
  constructor(private http: HttpClient, private profileDetails: UploadpostService, public dialog:MatDialog) { }

  ngOnInit(){

    this.getProfileDetails(this.id)

    console.log(this.images)    
  }

  getProfileDetails(id: any) {
    console.log('started')
    this.profileDetails.getProfileDetails(id).subscribe((res: {statusCode: number, msg: string, profileData: any, mediaData: any, postCount: any} )=> {
      console.log(res.profileData)
      console.log(res.mediaData)


      this.details =  res.profileData
      this.media = res.mediaData
      this.numberOfPosts = res.postCount
      this.images = res.mediaData.media

    })


  }

}
