import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserService } from 'src/app/services/user.service';
import { NgForm } from '@angular/forms';
import { LoginService } from 'src/app/services/login.service';




@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private loginService: LoginService) { }

  submit(loginForm: any) {
    this.loginService.userLogin(loginForm).subscribe((res: { statusCode: Number, msg: string }) => {
      console.log(res.msg, res.statusCode)
      console.log(loginForm)
      let errorMessage = document.getElementById('error') as HTMLElement;
      errorMessage.innerHTML = res.msg

    })
  }
}
