import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UploadpostService } from '../../services/uploadpost.service';

@Component({
  selector: 'app-user-home',
  templateUrl: './user-home.component.html',
  styleUrls: ['./user-home.component.css']
})
export class UserHomeComponent {
  posts : any[] = []
  postCard = document.getElementById('post-card');
  userId:any = localStorage.getItem('userId')

  constructor(private router: Router,private uploadService : UploadpostService){ }

  ngOnInit(){
    this.getPosts()
  }

  getPosts(){
    this.uploadService.viewPost().subscribe((res: {statusCode: Number, msg: string, username_id :Number , data: any} )=> {
      this.posts = res.data.reverse()
      console.log(this.posts)
    })

  }

  doubleTapLike(){
    console.log('double clicked')
    this.toggleLike
  }

  toggleLike(heartIcon: HTMLElement,postId:number): void {
    console.log(postId)

    this.uploadService.likePost(this.userId,postId).subscribe((res: {statusCode:Number,msg:string})=>{
      console.log(res)
      if (res.statusCode== 204){
        heartIcon.classList.toggle('liked');
      } 
    })  

  }



}
