import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-post-modal',
  templateUrl: './post-modal.component.html',
  styleUrls: ['./post-modal.component.css'],
})
export class PostModalComponent {
  imageURL:any;
  uploadPostService: any;

  constructor(
    @Inject(MAT_DIALOG_DATA) public data: any,
    public dialogRef: MatDialogRef<PostModalComponent>, private upload
  ) {
    this.imageURL = data;
    console.log('image url is',this.imageURL)

  }

  close(): void {
    this.dialogRef.close();
  }

  addComment(){
    let comment : any = document.getElementById('comment') as HTMLInputElement
    console.log(comment.value)
    let currentUser:any = localStorage.getItem('sessionUser')
    console.log(currentUser)

    let formData = new FormData()

    formData.append('comment',comment)
    formData.append('userId',currentUser)

    this.uploadPostService.uploadPost(formData).subscribe((res: { statusCode: Number, msg: string, username_id :Number }) => {
      console.log(res)
    })
  }
}
