import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-nav',
  templateUrl: './user-nav.component.html',
  styleUrls: ['./user-nav.component.css']
})
export class UserNavComponent {
  data : any
  id = localStorage.getItem('userId')

  constructor(private router:Router){ }

  ngOnInit(){
    this.getProfilePicture()
  }



  goToCreatePost(){
    this.router.navigateByUrl('/user/new')
  }

  logout(){
    localStorage.removeItem('sessionUser')
    localStorage.removeItem('userId')
    this.router.navigateByUrl('')
  }

  getProfilePicture(id : number){

    console.log(id)
  }
}
<div class="nav">
    <div class="left">
        <a routerLink="">
            <img class="logo" src="assets/images/logo.png" alt="">
        </a>
        <div class="search">
            <input id="search" type="text" placeholder="Search Here">
            <button><img id="search-icon" src="http://127.0.0.1:8000{{data.profilePicture}}" alt="search icon"></button>
        </div>

    </div>

    <div class="right">
        <a routerLink="/user/new"><button id="createPost">Create Post</button></a>
        <button mat-icon-button [matMenuTriggerFor]="menu" id="profile-button">
            <img id="profile_picture" src="assets/images/fortnite_x_travis_scott.jpg" alt="profile picture">
        </button>
        <mat-menu  #menu="matMenu">
            <a mat-menu-item >Dashboard</a>
            <a mat-menu-item (click)="goToCreatePost()">Create Post</a>
            <a mat-menu-item>Settings</a>
            <a mat-menu-item (click)="logout()">Log Out</a>

        </mat-menu>
    </div>
</div>
