import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UploadpostService } from '../../services/uploadpost.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-user-nav',
  templateUrl: './user-nav.component.html',
  styleUrls: ['./user-nav.component.css']
})
export class UserNavComponent {
  data: any
  id: any = localStorage.getItem('userId')
  searchKey: any = ''
  searchResults: any[] = []

  constructor(private router: Router, private uploadPost: UploadpostService, private userService: UserService) { }

  ngOnInit() {
    this.getProfilePicture(this.id)
  }

  search() {
    this.uploadPost.searchProfile(this.searchKey).subscribe((res: { data: [], msg: string, statusCode: number }) => {

      this.userService.searchResults(res.data)

      let currentUser = localStorage.getItem('sessionUser')

      for (let result in this.searchResults) {
      this.searchResults = res.data
        if (this.searchResults[result].username == currentUser) {
          let filteredResults= this.searchResults[result]
        }
      }

      this.router.navigateByUrl('/search')
    })
  }

  goToCreatePost() {
    this.router.navigateByUrl('/user/new')
  }

  logout() {
    localStorage.removeItem('sessionUser')
    localStorage.removeItem('userId')
    this.router.navigateByUrl('')
  }

  getProfilePicture(id: number) {
    this.uploadPost.getProfilePicture(id).subscribe((res: { profilePicture: any }) => {
      this.data = res.profilePicture

    });
  }
}
<div class="nav">
    <div class="left">
        <a routerLink="">
            <img class="logo" src="assets/images/logo.png" alt="">
        </a>
        <div class="search">
            <input [(ngModel)]="searchKey" id="searchKey" type="text" placeholder="Search Here">
            <button (click)="search()">
              <img id="search-icon" src="assets/images/search-icon.svg" alt="search icon">
            </button>
          </div>

    </div>

    <div class="right">
        <a routerLink="/user/new"><button id="createPost">Create Post</button></a>
        <button mat-icon-button [matMenuTriggerFor]="menu" id="profile-button">
            <img id="profile_picture" src="http://127.0.0.1:8000/media/{{data.image}}" alt="profile picture">
        </button>
        <mat-menu  #menu="matMenu">
            <a mat-menu-item >settings</a>
            <a mat-menu-item (click)="goToCreatePost()">Create Post</a>
            <a routerLink="/user/profile" mat-menu-item>Profile</a>
            <a mat-menu-item (click)="logout()">Log Out</a>

        </mat-menu>
    </div>
</div>
