import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UploadpostService } from '../../services/uploadpost.service';

@Component({
  selector: 'app-user-home',
  templateUrl: './user-home.component.html',
  styleUrls: ['./user-home.component.css']
})
export class UserHomeComponent {
  posts : any[] = []

  constructor(private router: Router,private uploadService : UploadpostService){ }

  ngOnInit(){
    this.getPosts()
  }

  getPosts(){
    this.uploadService.viewPost().subscribe((res: {statusCode: Number, msg: string, username_id :Number , data: any} )=> {
      this.posts = res.data.reverse()
    })
  }

}
