import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { UploadpostService } from 'src/app/user/services/uploadpost.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  data : any
  constructor(private http : HttpClient , private profileDetails : UploadpostService){}

  getProfileDetails(id :any){
    const userId = localStorage.getItem('userId')
    this.profileDetails.getProfilePicture(id).subscribe((res: {username: string , media : any, bio }) => {
      this.data = res

    });

  }

}
